import { defineComponent, ref, h, reactive, createCommentVNode, resolveComponent } from 'vue';
import XEUtils from 'xe-utils';
import { getConfig, usePermission, useSize } from '../../ui';
import { getSlotVNs } from '../../ui/src/vn';
export default defineComponent({
    name: 'VxeLink',
    props: {
        href: String,
        target: String,
        status: String,
        title: [String, Number],
        icon: String,
        routerLink: Object,
        underline: {
            type: Boolean,
            default: () => getConfig().link.underline
        },
        /**
         * 权限码
         */
        permissionCode: [String, Number],
        content: [String, Number],
        size: { type: String, default: () => getConfig().link.size || getConfig().size }
    },
    emits: [],
    setup(props, context) {
        const { slots } = context;
        const xID = XEUtils.uniqueId();
        const { computeSize } = useSize(props);
        const { computePermissionInfo } = usePermission(props);
        const refElem = ref();
        const reactData = reactive({});
        const refMaps = {
            refElem
        };
        const computeMaps = {};
        const $xeLink = {
            xID,
            props,
            context,
            reactData,
            getRefMaps: () => refMaps,
            getComputeMaps: () => computeMaps
        };
        const renderContent = () => {
            const { icon, content } = props;
            const defaultSlot = slots.default;
            const iconSlot = slots.icon;
            const textContent = XEUtils.toValueString(content);
            return [
                iconSlot || icon
                    ? h('span', {
                        class: 'vxe-link--icon'
                    }, iconSlot
                        ? getSlotVNs(iconSlot({}))
                        : [
                            h('i', {
                                class: icon
                            })
                        ])
                    : createCommentVNode(),
                defaultSlot || textContent
                    ? h('span', {
                        class: 'vxe-link--content'
                    }, defaultSlot ? defaultSlot({}) : textContent)
                    : createCommentVNode()
            ];
        };
        const renderVN = () => {
            const { status, target, href, title, underline, routerLink } = props;
            const permissionInfo = computePermissionInfo.value;
            const vSize = computeSize.value;
            if (!permissionInfo.visible) {
                return createCommentVNode();
            }
            if (routerLink) {
                return h(resolveComponent('router-link'), {
                    class: ['vxe-link', {
                            [`size--${vSize}`]: vSize,
                            [`theme--${status}`]: status,
                            'is--underline': underline
                        }],
                    title,
                    target,
                    to: routerLink
                }, {
                    default() {
                        return renderContent();
                    }
                });
            }
            return h('a', {
                ref: refElem,
                href,
                target,
                title,
                class: ['vxe-link', {
                        [`size--${vSize}`]: vSize,
                        [`theme--${status}`]: status,
                        'is--underline': underline
                    }]
            }, renderContent());
        };
        $xeLink.renderVN = renderVN;
        return $xeLink;
    },
    render() {
        return this.renderVN();
    }
});
