import { defineComponent, h, provide, inject, computed } from 'vue';
import XEUtils from 'xe-utils';
import { getConfig, createEvent, useSize } from '../../ui';
import VxeRadioComponent from './radio';
import VxeRadioButtonComponent from './button';
export default defineComponent({
    name: 'VxeRadioGroup',
    props: {
        modelValue: [String, Number, Boolean],
        disabled: {
            type: Boolean,
            default: null
        },
        type: String,
        options: Array,
        optionProps: Object,
        strict: { type: Boolean, default: () => getConfig().radioGroup.strict },
        size: { type: String, default: () => getConfig().radioGroup.size || getConfig().size }
    },
    emits: [
        'update:modelValue',
        'change'
    ],
    setup(props, context) {
        const { slots, emit } = context;
        const $xeForm = inject('$xeForm', null);
        const formItemInfo = inject('xeFormItemInfo', null);
        const xID = XEUtils.uniqueId();
        const computeIsDisabled = computed(() => {
            const { disabled } = props;
            if (disabled === null) {
                if ($xeForm) {
                    return $xeForm.props.readonly || $xeForm.props.disabled;
                }
                return false;
            }
            return disabled;
        });
        const computeMaps = {
            computeIsDisabled
        };
        const $xeradiogroup = {
            xID,
            props,
            context,
            name: XEUtils.uniqueId('xegroup_'),
            getComputeMaps: () => computeMaps
        };
        const computePropsOpts = computed(() => {
            return props.optionProps || {};
        });
        const computeLabelField = computed(() => {
            const propsOpts = computePropsOpts.value;
            return propsOpts.label || 'label';
        });
        const computeValueField = computed(() => {
            const propsOpts = computePropsOpts.value;
            return propsOpts.value || 'value';
        });
        const computeDisabledField = computed(() => {
            const propsOpts = computePropsOpts.value;
            return propsOpts.disabled || 'disabled';
        });
        let radioGroupMethods = {};
        useSize(props);
        const radioGroupPrivateMethods = {
            handleChecked(params, evnt) {
                emit('update:modelValue', params.label);
                radioGroupMethods.dispatchEvent('change', params, evnt);
                // 自动更新校验状态
                if ($xeForm && formItemInfo) {
                    $xeForm.triggerItemEvent(evnt, formItemInfo.itemConfig.field, params.label);
                }
            }
        };
        radioGroupMethods = {
            dispatchEvent(type, params, evnt) {
                emit(type, createEvent(evnt, { $radioGroup: $xeradiogroup }, params));
            }
        };
        const renderVN = () => {
            const { options, type } = props;
            const defaultSlot = slots.default;
            const valueField = computeValueField.value;
            const labelField = computeLabelField.value;
            const disabledField = computeDisabledField.value;
            const btnComp = type === 'button' ? VxeRadioButtonComponent : VxeRadioComponent;
            return h('div', {
                class: 'vxe-radio-group'
            }, defaultSlot
                ? defaultSlot({})
                : (options
                    ? options.map(item => {
                        return h(btnComp, {
                            label: item[valueField],
                            content: item[labelField],
                            disabled: item[disabledField]
                        });
                    })
                    : []));
        };
        Object.assign($xeradiogroup, radioGroupPrivateMethods, {
            renderVN,
            dispatchEvent
        });
        provide('$xeRadioGroup', $xeradiogroup);
        return renderVN;
    }
});
